$(document).ready(function(){$(".main__upArrow").on("click",function(o){if(""!==this.hash){o.preventDefault();var n=this.hash;$("html, body").animate({scrollTop:$(n).offset().top},800,function(){window.location.hash=n})}})});
jQuery(function($){$(window).scroll(function(){var bottomOffset = $(window).height();if($(document).scrollTop() > (0.5*bottomOffset)){$('.main__upArrow').addClass('arrowPosition');}else if($(document).scrollTop() < (0.5*bottomOffset)){$('.main__upArrow').removeClass('arrowPosition');}if(($(document).scrollTop() + bottomOffset+300) > ($(document).height())){$('.main__upArrow').removeClass('arrowPosition');}});});
jQuery(function($){$('#true_loadmore').click(function(){$(this).text('Загружаю...');var data = {'action': 'loadmore','query': true_posts,'page' : current_page};$.ajax({url:ajaxurl,data:data,type:'POST',success:function(data){if( data ) {$('#true_loadmore').text('Загрузить ещё').before(data);current_page++;if (current_page == max_pages) $("#true_loadmore").remove();} else {$('#true_loadmore').remove();}}});});});