var $page = $('html, body');$('a[href*="#"]').click(function() {$page.animate({scrollTop: $($.attr(this, 'href')).offset().top}, 400);return false;});
jQuery(function($){$(window).scroll(function(){var bottomOffset = $(window).height();if($(document).scrollTop() > (0.5*bottomOffset)){$('.main__upArrowDouble').addClass('arrowPosition').css('display', 'block');}else if($(document).scrollTop() < (0.5*bottomOffset)){$('.main__upArrowDouble').removeClass('arrowPosition').css('display', 'none');}if(($(document).scrollTop() + bottomOffset+300) > ($(document).height())){$('.main__upArrowDouble').removeClass('arrowPosition').css('display', 'none');}});});jQuery(function($){$('#true_loadmore').click(function(){$(this).text('Загружаю...');var data = {'action': 'loadmore','query': true_posts,'page' : current_page};$.ajax({url:ajaxurl,data:data,type:'POST',success:function(data){if( data ) {$('#true_loadmore').text('Загрузить ещё').before(data);current_page++;if (current_page == max_pages) $("#true_loadmore").remove();} else {$('#true_loadmore').remove();}}});});});